word'];            $result = $dbh->prepare("SELECT * FROM tb_users WHERE username= :username AND password= :password");            $result->bindParam(':username', $username);            $result->bindParam(':password', $password);            $result->execute();            $rows = $result->fetch(PDO::FETCH_NUM);            if ($rows > 0) {                $result = $dbh->prepare("SELECT * FROM tb_users WHERE username=:username");                $result->bindParam(':username', $username);                $result->execute();                while ($row = $result->fetch(PDO::FETCH_ASSOC)) {                    $res_id = $row['id'];                    $curr_status = $row['user_status'];                }                if ($curr_status == 'deactive') {                    $message = "Sorry <b>$username</b>, your account is temporarily deactivated by the admin.";                } else {                    $_SESSION['id'] = $res_id;                    header("location: admin/index.php?logid=$res_id");                }            } else {                $message = 'Username and Password are not exists.';            }        }        ?>        <table align="center">            <tr>                <td>                    <div id="login-style">                        <div id="login-header-style">                            User Login                        </div>                        <form class="form-horizontal" action="index.php" method="post">                            <div class="form-group">                                <label for="name">Username</label>                                <input type="text" name="username" id="username" value="" placeholder="Username" autofocus required style="height: 30px;" />                            </div>                            <div class="form-group">                                <label for="name">Password</label>                                <input type="password" value="" name="password" id="password" placeholder="Password" required style="height: 30px;" />                            </div>                            <div><?phpif (!empty($message)) {    echo "<p style='color: red; padding: 2px;'>" . $message . "</p>";}?>                            </div>                            <div class="control-group" style="margin-top: 10px;">                                <input type="submit" name="submit" value="Access Account" class="btn btn-primary"/>                            </div>                        </form>                    </div>                </td>            </tr>        </table>    </body></html>